import { useState } from 'react'
import MinMax from './MinMax'
import Total from './Total'


function booksStub() {
  return [
    {
      id: 1,
      title: 'Война и мир - Л.Н.Толстой',
      price: 800,
      rest: 10,
      quantity: 1,
    },
    {
      id: 2,
      title: 'Две жизни - К.Е.Антарова',
      price: 700,
      rest: 5,
      quantity: 1,
    },
    {
      id: 3,
      title: 'Разговор с богом - Н.Д.Уолша',
      price: 1000,
      rest: 2,
      quantity: 1,
    },
    {
      id: 5,
      title: 'Хохот Шамана - В.П.Серкин',
      price: 600,
      rest: 8,
      quantity: 1,
    },
    {
      id: 4,
      title: 'Хроники Ехо - Макс Фрай',
      price: 400,
      rest: 8,
      quantity: 1,
    },
  ]
}
export default function BookCart() {
  const [books, setBooks] = useState(booksStub())
  const [total, setTotal] = useState(0)

  const setQuantity = (id, quantity) => {

    setBooks(
      books.map((book) => (book.id !== id ? book : { ...book, quantity }))
    )
    setTotalQuantity(id)
    }
    //  (
const setTotalQuantity = (id) => {
    
    setTotal(
      total + parseInt(books[id].quantity) * parseInt(books[id].price)
      )
    }
      //book) => (sum += parseInt(book.quantity) * parseInt(book.price}).reverse()[0]


//   const setTotalQuantity = () => {
//     let sum
//     setTotal(
        
//     )
// for (var i in books) {
//   sum += books[i].quantity*books[i].price;
// }
// console.log(sum)
//   }

  return (
    <div className="some">
      <h1>books list</h1>
      <table>
        <tbody>
          <tr>
            <th>#</th>
            <th>Title</th>
            <th>Price</th>
            <th>Quantity</th>
            <th>Total</th>
       
          </tr>
          {books.map((book, i) => (
            <tr key={book.id}>
              <td>{i + 1}</td>
              <td>{book.title}</td>
              <td>{book.price}</td>
              <td>
                <MinMax
                  max={book.rest}
                  current={book.quantity}
                  onChange={(quantity) => {
                    setQuantity(book.id, quantity)
                    }}
                />
              </td>
              <td>{book.price * book.quantity}</td>
            </tr>
         
          ))}
             <tr>
              <Total booksCart={books.reduce((result, item) => {
                result += item
              }, 0)}/>
            </tr>
        </tbody>
      </table>
    </div>
  )
}

